mapper.select:
select ext.*,dt.name typeName from ( select id, dev_id, tpl_type_id,data_info from gis_dev_ext gdx
delete_flag = FALSE
and dev_id in ( SELECT id FROM share_dev WHERE type_id IN ( WITH RECURSIVE A AS ( SELECT A .* FROM share_dev_type A WHERE ID = #{dto.typeId, jdbcType=BIGINT} AND del_flag = 0 UNION ALL SELECT b.* FROM share_dev_type b, A WHERE A.ID = b.p_id AND b.del_flag = 0 ) SELECT ID FROM A ) )
and
${item.assemblyStr}
and ${dto.assemblyStr}
and EXISTS (select 1 from (SELECT regexp_split_to_table(#{devIds},',') AS dev_id) AS vir where cast(vir.dev_id as BIGINT) = gdx.dev_id)
) ext left join ( select dv.id ,dv.type_id,dvt.name from share_dev dv left join share_dev_type dvt on dv.type_id = dvt.id ) dt on ext.dev_id = dt.id

select count(*) from ( select id, dev_id, tpl_type_id,data_info from gis_dev_ext gdx
delete_flag = FALSE
and dev_id in ( SELECT id FROM share_dev WHERE type_id IN ( WITH RECURSIVE A AS ( SELECT A .* FROM share_dev_type A WHERE ID = #{dto.typeId, jdbcType=BIGINT} AND del_flag = 0 UNION ALL SELECT b.* FROM share_dev_type b, A WHERE A.ID = b.p_id AND b.del_flag = 0 ) SELECT ID FROM A ) )
and
${item.assemblyStr}
and ${dto.assemblyStr}
and EXISTS (select 1 from (SELECT regexp_split_to_table(#{devIds},',') AS dev_id) AS vir where cast(vir.dev_id as BIGINT) = gdx.dev_id)
) ext left join ( select dv.id ,dv.type_id,dvt.name from share_dev dv left join share_dev_type dvt on dv.type_id = dvt.id ) dt on ext.dev_id = dt.id
