mapper.select:
        select * from (
		    with recursive a as (
			    select a.* FROM share_dev_type a where p_id = #{pID,jdbcType=BIGINT}
				    and del_flag = 0
               union all
               select b.* from share_dev_type b,a where a.id = b.p_id
				    and b.del_flag = 0
             ) select * from a
		) rs where rs.limb_leaf = 2

SELECT * FROM ( SELECT devId, name, typeName, addr, dataInfo FROM ( SELECT sd.ID devId, sd.name, sd.addr, sdt.name typeName FROM ( SELECT * FROM share_dev sd WHERE type_id IN ( SELECT id FROM ( WITH RECURSIVE A AS ( SELECT A.* FROM share_dev_type A WHERE p_id = #{dto.typeId,jdbcType=BIGINT} AND del_flag = 0 UNION ALL SELECT b.* FROM share_dev_type b, A WHERE A.ID = b.p_id AND b.del_flag = 0 ) SELECT * FROM A ) rs WHERE rs.limb_leaf = 2 )
and EXISTS (select 1 from (SELECT regexp_split_to_table(#{devIds, jdbcType=VARCHAR},',') AS dev_id) AS vir where cast(vir.dev_id as BIGINT) = sd.id)
) sd LEFT JOIN share_dev_type sdt ON sd.type_id = sdt.ID AND sd.del_flag = 0 ) dv INNER JOIN ( SELECT dev_id, data_info dataInfo FROM gis_dev_ext WHERE delete_flag = FALSE ) b ON dv.devId = b.dev_id ) as jj

SELECT count(*) FROM ( SELECT devId, name, typeName, addr, dataInfo FROM ( SELECT sd.ID devId, sd.name, sd.addr, sdt.name typeName FROM ( SELECT * FROM share_dev sd WHERE type_id IN ( SELECT id FROM ( WITH RECURSIVE A AS ( SELECT A.* FROM share_dev_type A WHERE p_id = #{dto.typeId,jdbcType=BIGINT} AND del_flag = 0 UNION ALL SELECT b.* FROM share_dev_type b, A WHERE A.ID = b.p_id AND b.del_flag = 0 ) SELECT * FROM A ) rs WHERE rs.limb_leaf = 2 )
and EXISTS (select 1 from (SELECT regexp_split_to_table(#{devIds},',') AS dev_id) AS vir where cast(vir.dev_id as BIGINT) = sd.id)
) sd LEFT JOIN share_dev_type sdt ON sd.type_id = sdt.ID AND sd.del_flag = 0 ) dv LEFT JOIN ( SELECT dev_id, data_info dataInfo FROM gis_dev_ext WHERE delete_flag = FALSE ) b ON dv.devId = b.dev_id ) as jj

select count(1) num from gis_dev_ext gdx
where delete_flag = false

    and EXISTS (select 1 from
    (SELECT regexp_split_to_table(#{devIds},',') AS dev_id) AS vir  where cast (vir.dev_id as BIGINT) = gdx.dev_id)



        and  caliber > #{min,jdbcType=INTEGER}


        and  caliber >= #{min,jdbcType=INTEGER}




        and  caliber < #{max,jdbcType=INTEGER}


        and  caliber <= #{min,jdbcType=INTEGER}

select firstTypeid id,firstTypeName coverageName,count(1) number from (
    select ccc.*,ddd.* from (
        select dev_id,type_id from (
        SELECT dev_id FROM gis_dev_ext gdx

                1=1

                    and EXISTS (select 1 from
                    (SELECT regexp_split_to_table(#{devIds},',') AS dev_id) AS vir  where cast(vir.dev_id as BIGINT) = gdx.dev_id)


        ) a INNER JOIN share_dev b on a.dev_id = b.id
    ) ccc left join (
        select bcd.*,tc.name firstTypeName from (
            select id,name,path,path[1] firstTypeId from (
                with RECURSIVE t(id,name,path)  as (
                SELECT a.id,a.name,array[a.id] FROM share_dev_type a where p_id = -1
                union all
                select b.id,b.name,PATH||b.id from share_dev_type b , t c where c.id = b.p_id
                ) select * from t
            ) ac
        ) bcd left join
        (select id,name from share_dev_type where del_flag = 0) tc on bcd.firstTypeId = tc.id
    ) ddd on ccc.type_id = ddd.id
) fff group by firsttypeId,firsttypename
